import UniHeader from "../UniHeader/UniHeader";
import UniMenu from "../UniMenu/UniMenu";
import editIcon from '../../images/edit.svg';
import deleteIcon from '../../images/delete.svg';
import Menu from "../Menu/Menu";
import { useDispatch, useSelector } from 'react-redux';
import { getAllComments } from "../../redux/action";
import { useEffect } from "react";
const UniComments = () =>{
    document.body.classList.add('grey');
    const dispatch = useDispatch();
    const comments = useSelector(state => state.commentList);
    useEffect(() => {
        dispatch(getAllComments())
    },[])
    function toTime(time){
        const date = new Date(time);
        const timestampWithOffset = date.getTime();
        const offset = date.getTimezoneOffset() * 60 * 1000;
        const timestampWithoutOffset = timestampWithOffset - offset;
        const dateWithOffset = new Date(timestampWithOffset);
        console.log(dateWithOffset)
        return dateWithOffset
    }
    return (
        <>
            <Menu />
            <div className="UniContainer">
                <UniHeader />
                <div className="bodyContainer">
                    <UniMenu />
                    <div className="commentContainer">
                        <h2>COMMENTS</h2>
                        <div className="body">
                            {
                                comments.map((list) => (

                                    <div className="commentEl left">
                                        <div className="userName">
                                                {list.userName}
                                        </div>
                                        <div className="commentBubble">
                                            {list.comment}
                                            <div className="time">{list.updatedAt}</div>
                                        </div>
                                    </div>
                                ))
                            }
                            <div className="commentEl left">
                                <div className="userName">
                                        Rajesh Ravichandhran
                                </div>
                                <div className="commentBubble">
                                    The Computer Science program at Northeastern University is very intersting
                                    <div className="time">10:09PM</div>
                                </div>
                            </div>
                            <div className="commentEl right">
                                <div className="userName">
                                        Nethra Viswanathan
                                </div>
                                <div className="commentBubble">
                                    <div className="currUser">
                                        <div>The Software Engineering program too</div>
                                        <div>
                                            <img src={editIcon} alt="Edit Icon" height="20" />
                                            <img src={deleteIcon} alt="Delete Icon" height="20" />
                                        </div>
                                    </div>
                                    <div className="time">10:09PM</div>
                                </div>
                            </div>



                        </div>
                        <div className="addCommentSection">
                            <input type="text" name="" id=" " placeholder="Enter your comments"/>
                        </div>
                    </div>
                </div>
            </div>
        </>

    )
}

export default UniComments;
