import UniHeader from "../UniHeader/UniHeader";
import UniMenu from "../UniMenu/UniMenu";
import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getUniById } from "../../redux/action";
import { useParams } from "react-router-dom";
import Menu from "../Menu/Menu";
const UniAbout = () => {
    document.body.classList.add('grey');
    const dispatch = useDispatch();
    const uniDetsById = useSelector(state => state.uniWithId)
    console.log("1",uniDetsById);
    const { uniId } = useParams();
    useEffect(() => {
        console.log("1")
        dispatch(getUniById(uniId))
    },[])
    return (
        <>
        <Menu />
        <div className="UniContainer">
            <UniHeader details={uniDetsById}/>
            <div className="bodyContainer">
                <UniMenu />
                <div className="aboutContainer">
                    <h2>ABOUT</h2>
                    <h5>
                        The Northeastern University, Boston in the USA, is a private research university. Boston University School of Law was founded in 1898. First-year students attended the YMCA on Huntington Avenue. The school moved to its current home in Fenway - Kenmore Square and became "Northeastern College" before becoming a university in 2013.
                        The school's nine colleges, schools, and departments provide over 200 undergraduate degrees, over 100 graduate degrees, and PhD degrees in pharmacy, medicine and law, among others.
                    </h5>
                </div>
            </div>
        </div>
        </>

    )
} 

export default UniAbout;
