import Menu from "../Menu/Menu";
import toggleBtn from '../../images/toggle.svg'
import {useState } from 'react'
const Course = () => {
    document.body.classList.add('grey');
    state = {
        textflag: false,
    }
    const handleToggle = (id) => {
        this.setState({textflag : !this.state.textflag});
    };
    return (
        <>
            <Menu />
            <div className="courseParent">
                <div className="header">
                    Top Courses Preferred By Students
                </div>
                {Array(3).fill(1).map((el, i) =>
                     <div className="body">
                     <div className="toggleHead" onClick={() => {handleToggle()}}>
                         <div className="course">
                             MS in Computer Science
                         </div>
                         <div className="toggleBtn">
                             <img src={toggleBtn} alt="Open/Close Course" />
                         </div>
                     </div>
                     <div className={this.state.textflag ? "toggleBody show" : "toggleBody hide"}>
                         <ul>
                             <li>New Jersey Institute of Technology</li>
                             <li>Stevents Institute of Technology</li>
                             <li>University of Cincinnati</li>
                             <li>University of Illinois Chicago</li>
                             <li>University of Texas at Austin</li>
                         </ul>
                     </div>
                     {!this.state.textflag && this.props.text}
                 </div>
                )}


            </div>

        </>
    )
}
export default Course;
